<template>\n  <div class=\"enhanced-typing-indicator\" :class=\"containerClasses\">\n    <!-- Enhanced typing dots -->\n    <div v-if=\"variant === 'dots'\" class=\"typing-dots-enhanced\">\n      <div class=\"typing-dot-enhanced\" />\n      <div class=\"typing-dot-enhanced\" />\n      <div class=\"typing-dot-enhanced\" />\n    </div>\n    \n    <!-- AI thinking indicator -->\n    <div v-else-if=\"variant === 'thinking'\" class=\"ai-thinking-enhanced\">\n      <div class=\"thinking-icon\">ü§î</div>\n      <span class=\"thinking-text\">{{ message }}</span>\n    </div>\n    \n    <!-- Writing indicator -->\n    <div v-else-if=\"variant === 'writing'\" class=\"writing-indicator\">\n      <div class=\"writing-icon\">‚úçÔ∏è</div>\n      <span class=\"writing-text\">{{ message }}</span>\n      <div class=\"writing-line\" />\n    </div>\n    \n    <!-- Simple pulse -->\n    <div v-else class=\"simple-pulse\">\n      <div class=\"pulse-dot\" />\n      <span v-if=\"message\" class=\"pulse-text\">{{ message }}</span>\n    </div>\n  </div>\n</template>\n\n<script setup lang=\"ts\">\nimport { computed } from 'vue'\n\ninterface Props {\n  message?: string\n  variant?: 'dots' | 'thinking' | 'writing' | 'pulse'\n  size?: 'sm' | 'md' | 'lg'\n  compact?: boolean\n  animated?: boolean\n}\n\nconst props = withDefaults(defineProps<Props>(), {\n  message: 'AI is thinking...', \n  variant: 'dots',\n  size: 'md',\n  compact: false,\n  animated: true\n})\n\nconst containerClasses = computed(() => ({\n  'typing-compact': props.compact,\n  'typing-sm': props.size === 'sm',\n  'typing-lg': props.size === 'lg',\n  'typing-no-animation': !props.animated\n}))\n</script>\n\n<style scoped>\n/* Enhanced Typing Indicator Styles */\n.enhanced-typing-indicator {\n  display: inline-flex;\n  align-items: center;\n  gap: 0.5rem;\n  padding: 0.5rem 0.75rem;\n  border-radius: 1rem;\n  background: linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1));\n  backdrop-filter: blur(8px);\n  border: 1px solid rgba(139, 92, 246, 0.2);\n  transition: all 0.3s ease;\n}\n\n.enhanced-typing-indicator.typing-compact {\n  padding: 0.25rem 0.5rem;\n  gap: 0.25rem;\n}\n\n.enhanced-typing-indicator.typing-sm {\n  padding: 0.375rem 0.625rem;\n  font-size: 0.875rem;\n}\n\n.enhanced-typing-indicator.typing-lg {\n  padding: 0.75rem 1rem;\n  font-size: 1.125rem;\n}\n\n/* Enhanced Typing Dots */\n.typing-dots-enhanced {\n  display: flex;\n  gap: 0.375rem;\n  align-items: center;\n}\n\n.typing-dot-enhanced {\n  width: 8px;\n  height: 8px;\n  border-radius: 50%;\n  background: linear-gradient(135deg, #8b5cf6, #3b82f6);\n  animation: typingBounceEnhanced 1.8s ease-in-out infinite;\n  box-shadow: 0 2px 4px rgba(139, 92, 246, 0.3);\n}\n\n.typing-dot-enhanced:nth-child(1) { animation-delay: 0ms; }\n.typing-dot-enhanced:nth-child(2) { animation-delay: 200ms; }\n.typing-dot-enhanced:nth-child(3) { animation-delay: 400ms; }\n\n@keyframes typingBounceEnhanced {\n  0%, 60%, 100% {\n    transform: translateY(0) scale(0.8);\n    opacity: 0.4;\n  }\n  30% {\n    transform: translateY(-8px) scale(1.1);\n    opacity: 1;\n    box-shadow: 0 4px 8px rgba(139, 92, 246, 0.4);\n  }\n}\n\n/* AI Thinking Indicator */\n.ai-thinking-enhanced {\n  display: flex;\n  align-items: center;\n  gap: 0.5rem;\n  color: #6b7280;\n  font-size: 0.875rem;\n}\n\n.thinking-icon {\n  font-size: 1.25rem;\n  animation: thinkingRotate 3s ease-in-out infinite;\n}\n\n.thinking-text {\n  animation: thinkingPulse 2s ease-in-out infinite;\n}\n\n@keyframes thinkingRotate {\n  0%, 100% { transform: rotate(0deg); }\n  25% { transform: rotate(-5deg); }\n  75% { transform: rotate(5deg); }\n}\n\n@keyframes thinkingPulse {\n  0%, 100% { opacity: 0.6; }\n  50% { opacity: 1; }\n}\n\n/* Writing Indicator */\n.writing-indicator {\n  display: flex;\n  align-items: center;\n  gap: 0.5rem;\n  color: #6b7280;\n  font-size: 0.875rem;\n  position: relative;\n}\n\n.writing-icon {\n  font-size: 1.125rem;\n  animation: writing 2s ease-in-out infinite;\n}\n\n.writing-text {\n  position: relative;\n}\n\n.writing-line {\n  position: absolute;\n  bottom: -2px;\n  left: 0;\n  height: 1px;\n  background: linear-gradient(90deg, #8b5cf6, #3b82f6);\n  animation: writingLine 2s ease-in-out infinite;\n}\n\n@keyframes writing {\n  0%, 100% { transform: translateX(0) rotate(0deg); }\n  50% { transform: translateX(2px) rotate(2deg); }\n}\n\n@keyframes writingLine {\n  0% { width: 0%; }\n  50% { width: 100%; }\n  100% { width: 0%; }\n}\n\n/* Simple Pulse */\n.simple-pulse {\n  display: flex;\n  align-items: center;\n  gap: 0.5rem;\n  color: #6b7280;\n  font-size: 0.875rem;\n}\n\n.pulse-dot {\n  width: 8px;\n  height: 8px;\n  border-radius: 50%;\n  background: #8b5cf6;\n  animation: simplePulse 1.5s ease-in-out infinite;\n}\n\n.pulse-text {\n  animation: textFade 2s ease-in-out infinite;\n}\n\n@keyframes simplePulse {\n  0%, 100% { \n    opacity: 0.3;\n    transform: scale(0.8); \n  }\n  50% { \n    opacity: 1;\n    transform: scale(1.2); \n  }\n}\n\n@keyframes textFade {\n  0%, 100% { opacity: 0.7; }\n  50% { opacity: 1; }\n}\n\n/* Dark mode adjustments */\n@media (prefers-color-scheme: dark) {\n  .enhanced-typing-indicator {\n    background: linear-gradient(135deg, rgba(139, 92, 246, 0.15), rgba(59, 130, 246, 0.15));\n    border-color: rgba(139, 92, 246, 0.3);\n  }\n  \n  .ai-thinking-enhanced,\n  .writing-indicator,\n  .simple-pulse {\n    color: #9ca3af;\n  }\n}\n\n/* Reduced motion support */\n@media (prefers-reduced-motion: reduce) {\n  .enhanced-typing-indicator.typing-no-animation *,\n  .typing-no-animation .typing-dot-enhanced,\n  .typing-no-animation .thinking-icon,\n  .typing-no-animation .thinking-text,\n  .typing-no-animation .writing-icon,\n  .typing-no-animation .writing-line,\n  .typing-no-animation .pulse-dot,\n  .typing-no-animation .pulse-text {\n    animation: none !important;\n  }\n  \n  .typing-dot-enhanced {\n    opacity: 0.8;\n  }\n  \n  .pulse-dot {\n    opacity: 0.8;\n  }\n}\n\n/* Size variants */\n.typing-sm .typing-dot-enhanced {\n  width: 6px;\n  height: 6px;\n}\n\n.typing-lg .typing-dot-enhanced {\n  width: 10px;\n  height: 10px;\n}\n\n.typing-sm .thinking-icon,\n.typing-sm .writing-icon {\n  font-size: 1rem;\n}\n\n.typing-lg .thinking-icon,\n.typing-lg .writing-icon {\n  font-size: 1.5rem;\n}\n</style>"